// Fixed: Using HTTP methods with proper validation and secure practices
import java.net.HttpURLConnection;
import java.io.*;

public class SecureWebApp {
    public static void main(String[] args) {
        try {
            HttpURLConnection connection = (HttpURLConnection) new URL("http://example.com").openConnection();
            String requestedMethod = "GET"; // Validate method
            if (requestedMethod.equals("GET")) {
                connection.setRequestMethod(requestedMethod);
            } else {
                System.out.println("Invalid request method: " + requestedMethod);
                return;
            }
            connection.setDoOutput(false); // Secure: Do not allow output for GET requests
            connection.setRequestProperty("Content-Type", "application/x-www-form-urlencoded");
            String data = "username=admin&password=123456";
            // Secure: Only allow POST for sensitive data
            if (!requestedMethod.equals("POST")) {
                System.out.println("Data cannot be sent with GET method.");
                return;
            }
            try (OutputStream os = connection.getOutputStream()) {
                os.write(data.getBytes());
            }
            int responseCode = connection.getResponseCode();
            System.out.println("Response Code: " + responseCode);
        } catch (Exception e) {
            e.printStackTrace();
        }
    }
}
